// Resort component 
import React, { useEffect, useState } from 'react'
// Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Tab, Tabs } from 'react-bootstrap';
import { getWeatherData } from './data/weatherAPI'

const WeatherWidget = () => {
    // date 
    const date = new Date();
    const day1 = date.getDay();
    const day2 = date.getDay() + 1;
    const day3 = date.getDay() + 1;

    // weather Data
    const [weatherdata, setWeatherData] = useState(null);
    const getData = async () => {
        try {
            const data = await getWeatherData();
            setWeatherData(data);
        } catch (error) {
            console.log(error);
        }
    }
    useEffect(() => {
        getData();
    }, [])

    return (<div>
        <h6>Weather Forcast</h6>
        {
            weatherdata !== null ? (
                <Container>
                    <Row>
                        <div class="weatherBox">
                            {weatherdata.weather[0].description}<br />
                            <img src={'http://openweathermap.org/img/wn/' + weatherdata.weather[0].icon + '@2x.png'} /><br />
                            {weatherdata.weather[0].description} {weatherdata.main.temp}
                        </div>
                        <div class="weatherBox">
                            {weatherdata.weather[0].description}<br />
                            <img src={'http://openweathermap.org/img/wn/' + weatherdata.weather[0].icon + '@2x.png'} /><br />
                            {weatherdata.weather[0].description} {weatherdata.main.temp}
                        </div>
                        <div class="weatherBox">
                            {weatherdata.weather[0].description}<br />
                            <img src={'http://openweathermap.org/img/wn/' + weatherdata.weather[0].icon + '@2x.png'} /><br />
                            {weatherdata.weather[0].description} {weatherdata.main.temp}
                        </div>
                    </Row>
                </Container>
            ) : null
        }
    </div >);
}
export default WeatherWidget;
