// Resort component 
import React from 'react'
// Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';

// ***EXAMPLE RESORT IMAGE FOR TESTING***
import resortImg from '../../images/bows.png';

// ***HARD CODED / PLACEHOLDERS FOR TESTING***
// Default return for resort component 
const Resort = () => {
    return (
        <Container>
            <Row>
                <Col sm={4}>
                    <h4>*RESORT NAME PLACE HOLDER*</h4>
                    ***LINK TO WEBSITE***
                    <img src={resortImg} />
                </Col>
                <Col sm={8}>
                    <Row>
                        <div class="tab">
                            <button class="tablinks" onclick="openCity(event, 'London')">Description</button>
                            <button class="tablinks" onclick="openCity(event, 'Paris')">Mountain</button>
                            <button class="tablinks" onclick="openCity(event, 'Tokyo')">Weather</button>
                            <button class="tablinks" onclick="openCity(event, 'Tokyo')">Snow</button>
                        </div>
                    </Row>
                    <Row>
                    </Row>
                </Col>
            </Row>
        </Container>
    );
};

export default Resort
