// Resort component 
import React from 'react'
// Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown, Table } from 'react-bootstrap';
// Example resorts...
var resorts = [
    { name: "Pats Peak", price: "$100", rating: "5" },
    { name: "Wachusetts", price: "$60", rating: "4.5" },
    { name: "Nashoba", price: "$50", rating: "4.1" }
];
// Map for list resorts 
const listResorts = resorts.map((r) => (
    <tr key={r.name}>
        <td>{r.name}</td>
        <td></td>
        <td></td>
        <td>rating:{r.rating}</td>
        <td>price:{r.price}</td>
        <td><input type="checkbox"></input></td>
    </tr>
));

const SortBy = () => {
    return (
        <div class="sortBy">
            <Dropdown>
                <label class="lblSortBy">SortBy: </label>
                <Dropdown.Toggle variant="light" id="dropdown-basic">
                    Select
                </Dropdown.Toggle>
                <Dropdown.Menu>
                    <Dropdown.Item href="#">Distance</Dropdown.Item>
                    <Dropdown.Item href="#">Price</Dropdown.Item>
                    <Dropdown.Item href="#">Review</Dropdown.Item>
                </Dropdown.Menu>
                <div class="float-right">
                    <label class="lblCompare">Select Three Resorts: </label>
                    <button class="btnCompare btn btn-light">Compare</button>
                </div>
            </Dropdown>
        </div>
    )
};

const ResortList = () => {
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Distance</th>
                    <th>Description</th>
                    <th>Review</th>
                    <th>Price</th>
                    <th>Select</th>
                </tr>
            </thead>
            <tbody>
                {listResorts}
            </tbody>
        </Table>
    )
};




export { SortBy, ResortList }
