// Resort component 
import React, { useEffect, useState } from 'react'
// Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Tab, Tabs } from 'react-bootstrap';
// ***EXAMPLE RESORT IMAGE FOR TESTING***
import resortImg from '../../images/TrailMap.jpg';
import { getWeatherData } from './data/weatherAPI'
// ***HARD CODED / PLACEHOLDERS FOR TESTING***
// Tabs varibles
//var activities = (<div><p>***DESCRIPTION PLACE HOLDER***</p></div>);
//var events = (<div><p>***DESCRIPTION PLACE HOLDER***</p></div>);
//var reviews = (<div><p>***DESCRIPTION PLACE HOLDER***</p></div>);
//var prices = (<div><p>***DESCRIPTION PLACE HOLDER***</p></div>);
// Default return for resort component
const Resort = () => {
    const [weatherdata, setWeatherData] = useState(null);
    const [loading, setLoading] = useState(false);

    const getData = async () => {
        try {
            const data = await getWeatherData();
            setWeatherData(data);
            console.log(data);
        } catch (error) {
            console.log(error);
        }
    }
    useEffect(() => {
        getData();
    }, [])

    var description = (<div>
        <p>***DESCRIPTION PLACE HOLDER***</p>
    </div>);
    var mountain = (<div>
        <p>***MOUNTAIN PLACE HOLDER***</p>
    </div>);
    var weather = (<div>
        <h6>Weather Forcast</h6>
        <p>*** WEATHER PLACE HOLDER***</p>
    </div>);
    var snow = (<div>
        <p>***DESCRIPTION PLACE HOLDER***</p>
    </div>);

    return (
        <div class="box" >
            <Container fluid>
                {weatherdata.weather[0].main}
                <Row>
                    <Col xs={12} md={6} lg={4}>
                        <Row>
                            <h4>*RESORT NAME PLACE HOLDER*</h4>
                        </Row>
                        <Row>
                            ***LINK TO WEBSITE***
                    </Row>
                        <Row>
                            <img src={resortImg} alt="resortImg" style={{ width: "100%" }} />
                        </Row>
                    </Col>
                    <Col xs={12} md={6} lg={8}>
                        <div style={{ height: "50%" }}>
                            <Tabs defaultActiveKey="Description" id="uncontrolled-tab-example">
                                <Tab eventKey="Description" title="Description">
                                    {description}
                                </Tab>
                                <Tab eventKey="Mountain" title="Mountain">
                                    {mountain}
                                </Tab>
                                <Tab eventKey="Weather" title="Weather">

                                </Tab>
                                <Tab eventKey="Snow" title="Snow">
                                    {snow}
                                </Tab>
                            </Tabs>
                        </div>
                        <div>
                            <Tabs defaultActiveKey="Activities" id="uncontrolled-tab-example">
                                <Tab eventKey="Activities" title="Activities">
                                </Tab>
                                <Tab eventKey="Events" title="Events">
                                </Tab>
                                <Tab eventKey="Reviews" title="Reviews">
                                </Tab>
                                <Tab eventKey="Prices" title="Prices">
                                </Tab>
                            </Tabs>
                        </div>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};
export default Resort
