        }
    });
    return data;
}

const Home = () => {
    return (
        <div>
            <div className="aboutme">
                <p>
                    Please enter your zipcode or search up a resort you want to search for.
            </p>
            </div>
            <div className="background">
                <div className="searchbar">
                    <input type="text" id="searchResortInput" className="searchbar" placeholder="Please enter your zipcode or a Resort name" defaultValue=" "></input>
                    <button onClick={resortListFromApi} className="searchbutton">SUBMIT </button>
                </div>
            </div>
        </div>
    );
};

export default Home;
