// Resort component 
import React from 'react'
import { Link } from 'react-router-dom';
// Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';
import { Dropdown, Table } from 'react-bootstrap';
// BOWS API
import { getResortsData } from '../Resort/data/bowsAPI';

// Pull all resorts from database
var resorts;
const ResortListFromApi = async () => { resorts = await getResortsData(); };
ResortListFromApi();

/*// Map for list resorts 
const listResorts = resorts.map((r) => (
    <tr key={r.name}>
        <td><Link to="/resort"><b>{r.name}</b></Link></td>
        <td>{r.distance}</td>
        <td>{r.description}</td>
        <td>{r.rating}</td>
        <td>{r.price}</td>
        <td><input type="checkbox"></input></td>
    </tr>
));
*/

const SortBy = () => {
    return (
        <div className="sortBy">
            <Dropdown>
                <label className="lblSortBy">SortBy: </label>
                <Dropdown.Toggle variant="light" id="dropdown-basic">
                    Select
                </Dropdown.Toggle>
                <Dropdown.Menu>
                    <Dropdown.Item href="#">Distance</Dropdown.Item>
                    <Dropdown.Item href="#">Price</Dropdown.Item>
                    <Dropdown.Item href="#">Review</Dropdown.Item>
                </Dropdown.Menu>
                <div className="float-right">
                    <label className="lblCompare">Select Three Resorts: </label>
                    <button className="btnCompare btn btn-light">Compare</button>
                </div>
            </Dropdown>
        </div>
    )
};

const ResortList = () => {
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Distance</th>
                    <th>Description</th>
                    <th>Review</th>
                    <th>Price</th>
                    <th>Select</th>
                </tr>
            </thead>
            <tbody>
                {/*listResorts*/}
            </tbody>
        </Table>
    )
};

export { SortBy, ResortList }
