const Test = () => {
  const [resortData, setSetData] = useState(null);
  const getData = async () => {
    try {
      const data = await getResortsData();
      setSetData(data);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getData();
  }, [])

  return (
    <div>
      {
        resortData !== null ? (
          <p>{resortData[0].name}</p>
        ) : null
      }
    </div>
  );
}

export default Test;
